import React, { useEffect, useState } from 'react';
import axios from 'axios';

const TableSelection = ({ date, timeSlot, guests, onNext, onPrevious }) => {
    const [tables, setTables] = useState([]);
    const [selectedTable, setSelectedTable] = useState('');

    useEffect(() => {
        axios.get(`/reservations/available-tables?date=${date}&timeSlot=${timeSlot}&guests=${guests}`)
        .then(reponse => setTables(response.data))
        .catch(error => console.log(error));
    }, [date, timeSlot, guests]);

    const handleTableSelect = (table) => {
        setSelectedTable(table);
    }

    const handleNext = () => {
        onNext(selectedTable);
    }

    return(
        <div>
            <h1>Select a Table</h1>
            <div>
                {
                    tables.map(table => (
                        <button key={table.tableNumber} onClick={() => handleTableSelect(table._id)}>
                            Table {table.tableNumber} - Seats {table.seats}
                        </button>
                    ))
                }
            </div>
            <button onClick={onPrevious}>PREVIOUS</button>
            <button onClick={handleNext} disabled={!selectedTable}>NEXT</button>
        </div>
    );
};

export default TableSelection;
